angular.module('galaxy').run(['$templateCache', function($templateCache) {
  'use strict';

  $templateCache.put('templates/system.html',
    "<svg height=\"100%\" width=\"100%\" msd-wheel=\"zoom($event, $delta)\" ng-mousedown=\"mousedown($event)\" class=\"stellar-system-drawing\"><clippath><rect x=\"0\" y=\"0\" width=\"100%\" height=\"100%\"></clippath><g ng-attr-transform=\"translate({{x}}, {{y}}) scale({{z}})\"><g><circle ng-click=\"select(system.star)\" ng-attr-cx=\"0\" ng-attr-cy=\"0\" ng-attr-r=\"{{system.star.radius}}\" ng-attr-stroke=\"black\" ng-attr-stroke-width=\"1\" ng-attr-fill=\"{{system.star.color}}\"></circle><text x=\"0\" y=\"{{system.star.radius * 1.3}}\" text-anchor=\"middle\" baseline-shift=\"-0.5ex\" fill=\"black\" font-size=\"10\">{{system.star.name}}</text></g><g><g ng-repeat=\"planet in system.star.orbits\" class=\"planet-group\" ng-attr-transform=\"rotate({{getRotation(planet)}})\"><circle class=\"orbit planet-orbit\" ng-show=\"options.drawOrbits\" cx=\"0\" cy=\"0\" ng-attr-r=\"{{planet.distance}}\" stroke=\"black\" stroke-width=\".5\" fill=\"none\"></circle><g ng-show=\"options.drawPlanets\" ng-attr-transform=\"translate({{planet.distance}}, 0)\"><circle ng-click=\"select(planet)\" class=\"stellar-object planet-circle\" ng-attr-cx=\"0\" ng-attr-cy=\"0\" ng-attr-r=\"{{planet.size}}\" ng-attr-fill=\"{{planet.color}}\"></circle><g ng-repeat=\"moon in planet.orbits\" ng-attr-transform=\"rotate({{getRotation(moon)}})\"><circle class=\"orbit moon-orbit\" ng-show=\"options.drawMoonsOrbits\" cx=\"0\" cy=\"0\" ng-attr-r=\"{{moon.distance}}\" stroke=\"black\" stroke-width=\".2\" fill=\"none\"></circle><g class=\"moon\" ng-attr-transform=\"translate({{moon.distance}}, 0)\"><circle class=\"stellar-object moon-circle\" ng-show=\"options.drawMoons\" ng-attr-cx=\"0\" ng-attr-cy=\"0\" ng-attr-r=\"{{moon.size}}\" ng-attr-fill=\"{{moon.color}}\"></circle></g></g></g></g></g></g></svg>"
  );

}]);
